import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Typography,
  Button,
  TextField,
  List,
  ListItem,
  ListItemText,
  Paper,
  Box,
} from '@mui/material';

const App = () => {
  const [boards, setBoards] = useState([]);
  const [token, setToken] = useState('');
  const [boardName, setBoardName] = useState('');

  const login = async () => {
  try {
    const res = await axios.post('http://localhost:5000/api/auth/login', {
      email: 'test@example.com',
      password: 'test123',
    });
    setToken(res.data.token);
  } catch (err) {
    console.error('Login error:', err.response?.data || err.message);
    alert(`Login failed: ${err.response?.data || err.message}`);
  }
};
  const loadBoards = async () => {
    const res = await axios.get('http://localhost:5000/api/boards', {
      headers: { Authorization: `Bearer ${token}` },
    });
    setBoards(res.data);
  };

  const addBoard = async () => {
    if (!boardName.trim()) return;
    await axios.post('http://localhost:5000/api/boards',
      { name: boardName },
      { headers: { Authorization: `Bearer ${token}` } }
    );
    setBoardName('');
    loadBoards();
  };

  useEffect(() => {
    if (token) loadBoards();
  }, [token]);

  const addTask = async (boardId, text) => {
  if (!text.trim()) return;
  await axios.post(
    `http://localhost:5000/api/boards/${boardId}/tasks`,
    { text },
    { headers: { Authorization: `Bearer ${token}` } }
  );
  loadBoards();
};

const deleteTask = async (boardId, taskId) => {
  await axios.delete(
    `http://localhost:5000/api/boards/${boardId}/tasks/${taskId}`,
    { headers: { Authorization: `Bearer ${token}` } }
  );
  loadBoards();
};

const toggleTask = async (boardId, task) => {
  await axios.put(
    `http://localhost:5000/api/boards/${boardId}/tasks/${task._id}`,
    { completed: !task.completed },
    { headers: { Authorization: `Bearer ${token}` } }
  );
  loadBoards();
};


  return (
    <Container maxWidth="sm" sx={{ mt: 8 }}>
      <Typography variant="h4" align="center" gutterBottom>
        Task Boards
      </Typography>

      {!token ? (
        <Box textAlign="center">
          <Button variant="contained" color="primary" onClick={login}>
            Login as Test User
          </Button>
        </Box>
      ) : (
        <>
          <Box display="flex" gap={2} mb={2}>
            <TextField
              label="New Board"
              value={boardName}
              onChange={(e) => setBoardName(e.target.value)}
              fullWidth
            />
            <Button variant="contained" onClick={addBoard}>
              Add
            </Button>
          </Box>

          <Paper elevation={3} sx={{ p: 2 }}>
            <List>
              {boards.map((board) => (
                <ListItem key={board._id} sx={{ display: 'block' }}>
  <Typography variant="h6">{board.name}</Typography>

  <List dense>
    {board.tasks?.map((task) => (
      <ListItem
        key={task._id}
        secondaryAction={
          <Button size="small" onClick={() => deleteTask(board._id, task._id)}>
            Delete
          </Button>
        }
      >
        <ListItemText
          primary={task.text}
          onClick={() => toggleTask(board._id, task)}
          sx={{
            textDecoration: task.completed ? 'line-through' : 'none',
            cursor: 'pointer'
          }}
        />
      </ListItem>
    ))}
  </List>

  <Box display="flex" gap={1} mt={1}>
    <TextField
      size="small"
      placeholder="New Task"
      value={board.newTask || ''}
      onChange={(e) =>
        setBoards((prev) =>
          prev.map((b) =>
            b._id === board._id ? { ...b, newTask: e.target.value } : b
          )
        )
      }
    />
    <Button
      size="small"
      onClick={() => addTask(board._id, board.newTask)}
    >
      Add Task
    </Button>
  </Box>
</ListItem>

              ))}
            </List>
          </Paper>
        </>
      )}
    </Container>
  );
};

export default App;
